package com.revature.model;

public class SuperJoke {

	private String type;
	private SuperValue value;
	
	public SuperJoke() {
		super();
		// TODO Auto-generated constructor stub
	}
	public SuperJoke(String type, SuperValue value) {
		super();
		this.type = type;
		this.value = value;
	}
	public String getType() {
		return type;
	}
	public void setType(String type) {
		this.type = type;
	}
	public SuperValue getValue() {
		return value;
	}
	public void setValue(SuperValue value) {
		this.value = value;
	}
	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((type == null) ? 0 : type.hashCode());
		result = prime * result + ((value == null) ? 0 : value.hashCode());
		return result;
	}
	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		SuperJoke other = (SuperJoke) obj;
		if (type == null) {
			if (other.type != null)
				return false;
		} else if (!type.equals(other.type))
			return false;
		if (value == null) {
			if (other.value != null)
				return false;
		} else if (!value.equals(other.value))
			return false;
		return true;
	}
	@Override
	public String toString() {
		return "SuperJoke [type=" + type + ", value=" + value + "]";
	}
	
}
